mapper.select:
SELECT BASERESPONSIBILITY.RESPONSIBILITY_ID, BASERESPONSIBILITY.RESPONSIBILITY_NAME, BASERESPONSIBILITY.RESPONSIBILITY_CODE, BASERESPONSIBILITY.RESPONSIBILITY_DESC, TO_CHAR(BASERESPONSIBILITY.BEGIN_DATE,'YYYY-MM-DD') BEGIN_DATE, TO_CHAR(BASERESPONSIBILITY.END_DATE,'YYYY-MM-DD') END_DATE, BASERESPONSIBILITY.LAST_UPDATE_LOGIN, BASERESPONSIBILITY.LAST_UPDATED_BY, TO_CHAR(BASERESPONSIBILITY.LAST_UPDATE_DATE,'YYYY-MM-DD') LAST_UPDATE_DATE, BASERESPONSIBILITY.CREATED_BY, TO_CHAR(BASERESPONSIBILITY.CREATION_DATE,'YYYY-MM-DD') CREATION_DATE FROM AUPORTAL.BASE_RESPONSIBILITY_VL BASERESPONSIBILITY WHERE 1=1
AND BASERESPONSIBILITY.RESPONSIBILITY_NAME = #{responsibilityName}
AND BASERESPONSIBILITY.RESPONSIBILITY_CODE = #{responsibilityCode}
AND BASERESPONSIBILITY.RESPONSIBILITY_ID = #{responsibilityId}
ORDER BY RESPONSIBILITY_NAME DESC

SELECT BASERESPONSIBILITY.RESPONSIBILITY_ID, BASERESPONSIBILITY.RESPONSIBILITY_NAME, BASERESPONSIBILITY.RESPONSIBILITY_CODE, BASERESPONSIBILITY.RESPONSIBILITY_DESC, TO_CHAR(RU.BEGIN_DATE,'YYYY-MM-DD') BEGIN_DATE,
BASERESPONSIBILITY.LAST_UPDATE_LOGIN, BASERESPONSIBILITY.LAST_UPDATED_BY, TO_CHAR(BASERESPONSIBILITY.LAST_UPDATE_DATE,'YYYY-MM-DD') LAST_UPDATE_DATE, BASERESPONSIBILITY.CREATED_BY, TO_CHAR(BASERESPONSIBILITY.CREATION_DATE,'YYYY-MM-DD') CREATION_DATE, (SELECT ORG.ORG_ID FROM AUPORTAL.BASE_PROFILE_VL A, AUPORTAL.BASE_PROFILE_VALUE B, (SELECT CAB.ORG_ID ORG_ID, CAB.USER_ID ORG_ID_CHILD, CAB.SECONDARY_INVENTORY_NAME ORG_NAME_CHILD, CAB.CUST_ACCOUNT_ID LEVEL_TYPE FROM CUX.CUX_CDM_ACCESS_BASEDATA CAB WHERE CAB.ACCESS_TYPE = '9' AND CAB.CUST_ACCOUNT_ID='2') ORG WHERE A.PROFILE_ID = B.PROFILE_ID AND A.PROFILE_CODE = 'ORG_ID' AND B.PROFILE_VALUE = ORG.ORG_ID_CHILD AND B.RESPONSIBILITY_ID = BASERESPONSIBILITY.RESPONSIBILITY_ID )ORG_ID FROM AUPORTAL.BASE_RESPONSIBILITY_VL BASERESPONSIBILITY, BASE.BASE_RESPONSIBILITY_USER RU WHERE BASERESPONSIBILITY.RESPONSIBILITY_ID = RU.RESPONSIBILITY_ID AND RU.USER_ID = #{userId}
AND BASERESPONSIBILITY.RESPONSIBILITY_NAME = #{responsibilityName}
AND BASERESPONSIBILITY.RESPONSIBILITY_CODE = #{responsibilityCode}
AND BASERESPONSIBILITY.RESPONSIBILITY_ID = #{responsibilityId}
ORDER BY BASERESPONSIBILITY.RESPONSIBILITY_NAME ASC

select ro.organization_id as invOrganizationId, oi.INV_ORGANIZATION_NAME as invOrganizationName
  from BASE.BASE_RESPONSIBILITY_ORG_ACCESS   ro,
       auportal.Base_Organization_Inv_v oi
 where ro.organization_id = oi.INV_ORGANIZATION_ID
   and ro.responsibility_id = #{responsibilityId}
